settings.custom_css:
div#textsection {
    display: flex;
    flex-direction: column-reverse;
padding: 30px 15px;
}
.grid-product-text, .grid-product label.inline-block {
font-size: calc(var(--font-heading-scale) * 19px);
    line-height: calc(var(--font-heading-scale) * 26px);
text-transform: uppercase;
}
.lb-price-wrapper b {
font-weight: normal;
}
.btn-add-to-bundle, .btn-add-to-cart {
background-color: #473247;
    color: #fff;
    border: 2px solid #473247;
    font-size: calc(var(--font-body-scale) * 10px);
    line-height: normal;
    text-transform: uppercase;
    letter-spacing: 0px;
    padding: 8px 10px;
    text-align: center;
    cursor: pointer;
    border-radius: var(--buttons-radius-outset);
min-width: auto;
}
.btn-add-to-bundle:after, .btn-add-to-bundle:before, .btn-add-to-cart:after, .btn-add-to-cart:before {
display: none;
}
.btn-add-to-bundle:not([disabled]):hover, .btn-add-to-bundle:focus, .btn-add-to-cart:not([disabled]):hover, .btn-add-to-cart:focus {
background-color: transparent !important;
    color: #473247 !important;
}

@media screen and (max-width: 767.98px) {
.grid-product-text, .grid-product label.inline-block {
 font-size: calc(var(--font-heading-scale) * 18px);
    line-height: calc(var(--font-heading-scale) * 20px);

}
}


@media screen and (max-width: 575.98px) {
.grid-product-text, .grid-product label.inline-block {
 font-size: calc(var(--font-heading-scale) * 14px);
    line-height: calc(var(--font-heading-scale) * 20px);

}
}
